function [result] = effect()  pkg load image  #Load image file  origin = imread('IMG.jpg');  origin = imresize(origin, 0.25);  #Show the original image  subplot(2,1,1);  imshow(origin);  subplot(2,1,2);  #Perform changes  newimg = process(origin);  #Show the image with hidden text  imshow(newimg);  imwrite(newimg, 'out.bmp');endfunctionfunction [newimg] = process(origin)  newimg = origin;  for x=1:size(origin)(1)    for y=1:size(origin)(2)      #Засветляю и сжимаю динамический диапазон      newimg(x, y, 1)=newimg(x, y, 1)+60;      newimg(x, y, 2)=newimg(x, y, 2)+60;      newimg(x, y, 3)=newimg(x, y, 3)+60;    endfor  endforendfunction