#load imagepkg load image#task onefunction R = AverageWithTh(x, Th)  [r, c] = size(x);  n = r*c;  r = floor((r+1)/2); c = floor((c+1/2));  s = sum(x(:))/n;  if (abs(x(r,c) - s))> Th    R = s;    else    R = x(r,c);    endif    endfunction[IMAGE, map] = imread('./Parrot.bmp');gray_image = ind2gray(IMAGE, map);noicy_image = imnoise(gray_image,"salt & pepper");med_image = medfilt2(noicy_image);nel = nlfilter(double(gray_image), [3 3], @AverageWithTh, 0.2);subplot(2,2,1), imshow(gray_image);subplot(2,2,2), imshow(noicy_image);subplot(2,2,3), imshow(med_image);subplot(2,2,4), imshow(nel);#task 2. Processing gray_iageres1 = filter2([1 1 -1; 1 -2 -1; 1 1 -1], gray_image);figure; imshow(res1);res2 = filter2([1 1 1; 1 -2 -1; 1 -1 -1], gray_image);figure; imshow(res2);